mapper.update:
UPDATE TBL_BOARD
SET REPLYCNT = REPLYCNT + #{amount}
WHERE BNO = #{bno}

mapper.select:

SELECT * 
FROM (
	    SELECT 
	    /*+INDEX_DESC(TBL_BOARD SYS_C007210) */ 
	    ROWNUM RN, BNO, TITLE, CONTENT, WRITER, REGDATE, UPDATEDATE, REPLYCNT
	    FROM TBL_BOARD


		    	ROWNUM <= #{pageNum} * #{amount} 

	 )
WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.insert:


SELECT SEQ_BOARD.NEXTVAL FROM DUAL

INSERT INTO TBL_BOARD ( 
		BNO
  	   ,TITLE
  	   ,CONTENT
  	   ,WRITER
)
VALUES (
		#{bno}
	   ,#{title}
	   ,#{content}
	   ,#{writer}
)

mapper.update:
UPDATE TBL_BOARD
SET TITLE = #{title}
   ,CONTENT = #{content}
   ,WRITER = #{writer}
   ,UPDATEDATE = SYSDATE
WHERE BNO = #{bno}
